package floatdemo;

public class Circle {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		//声明一个变量radius代表圆形半径
		//双精度浮点型
		double radius=4;
		//声明一个变量pi代表圆周率
		double pi=3.14;
		//声明一个变量per代表圆形周长
		double per=2*pi*radius;
		//打印圆形周长和半径
		System.out.println("圆形的半径是"+radius);
		System.out.println("圆形的周长是"+per);
	}

}
