package intdemo;

public class IntDemo {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		//声明一个变量x代表长方形的宽度
		//int代表整型变量（整数）
		//一步完成变量x声明和赋值
		int x=10;
		//声明一个变量y代表长方形的长度
		int y=20;
		//声明一个变量z代表长方形的周长: z=2*(x+y)
		int z=2*(x+y);
		//把长方形的周长打印出来
		System.out.println(z);
		//打印长方形的宽度和长度
		System.out.println(x);
		System.out.println(y);
		
	}

}






